{"content": "import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatTabsModule } from '@angular/material/tabs';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatChipsModule } from '@angular/material/chips';
import { MatProgressBarModule } from '@angular/material/progress-bar';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';

interface ProjectDetails {
  id: string;
  name: string;
  description: string;
  status: 'active' | 'completed' | 'draft';
  progress: number;
  createdDate: Date;
  lastModified: Date;
  tags: string[];
  files: ProjectFile[];
  tasks: ProjectTask[];
}

interface ProjectFile {
  id: string;
  name: string;
  type: string;
  size: number;
  uploadDate: Date;
  status: 'uploaded' | 'processing' | 'processed' | 'error';
}

interface ProjectTask {
  id: string;
  title: string;
  status: 'pending' | 'in-progress' | 'completed';
  assignee?: string;
  dueDate?: Date;
}

@Component({
  selector: 'app-project-view',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatTabsModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatChipsModule,
    MatProgressBarModule
  ],
  template: `
    <div class="project-container" *ngIf="project">
      <!-- Header Section -->
      <div class="project-header">
        <div class="header-content">
          <button mat-icon-button (click)="goBack()" class="back-button">
            <mat-icon>arrow_back</mat-icon>
          </button>

          <div class="project-info">
            <h1 class="project-title">
              {{ isEditMode ? 'Edit Project' : project.name }}
            </h1>
            <div class="project-meta">
              <span class="status-chip" [class]="'status-' + project.status">
                {{ project.status | titlecase }}
              </span>
              <span class="project-id">ID: {{ project.id }}</span>
              <span class="last-modified">
                Modified: {{ project.lastModified | date:'short' }}
              </span>
            </div>
          </div>

          <div class="header-actions">
            <button mat-stroked-button *ngIf="!isEditMode" (click)="toggleEditMode()">
              <mat-icon>edit</mat-icon>
              Edit
            </button>
            <button mat-raised-button color="primary" *ngIf="isEditMode" (click)="saveProject()">
              <mat-icon>save</mat-icon>
              Save
            </button>
            <button mat-button *ngIf="isEditMode" (click)="cancelEdit()">
              Cancel
            </button>
          </div>
        </div>

        <mat-progress-bar 
          mode="determinate" 
          [value]="project.progress" 
          class="progress-bar">
        </mat-progress-bar>
      </div>

      <!-- Content Tabs -->
      <mat-tab-group class="project-tabs" dynamicHeight>
        <!-- Overview Tab -->
        <mat-tab label="Overview">
          <div class="tab-content">
            <div class="overview-grid">
              <mat-card class="overview-card">
                <mat-card-header>
                  <mat-card-title>Project Details</mat-card-title>
                </mat-card-header>
                <mat-card-content>
