/* Global Styles for Vibe Coding Accelerator Frontend */

/* Import Angular Material core styles and theme */
@use '@angular/material' as mat;

/* Include the common styles for Angular Material. We include this here so that you only
   have to load a single css file for Angular Material in your app. */
@include mat.core();

/* Define the palettes for your theme using the Material Design palettes available in palette.scss
   (imported above). For each palette, you can optionally specify a default, lighter, and darker
   hue. Available color palettes: https://material.io/design/color/ */
$frontend-primary: mat.define-palette(mat.$azure-palette);
$frontend-accent: mat.define-palette(mat.$blue-palette, A200, A100, A400);

/* The warn palette is optional (defaults to red). */
$frontend-warn: mat.define-palette(mat.$red-palette);

/* Create the theme object. A theme consists of configurations for individual
   theming systems such as "color" or "typography". */
$frontend-theme: mat.define-light-theme((
  color: (
    primary: $frontend-primary,
    accent: $frontend-accent,
    warn: $frontend-warn,
  ),
  typography: mat.define-typography-config(),
  density: 0,
));

/* Include theme styles for core and each component used in your app. */
@include mat.all-component-themes($frontend-theme);

/* Global Styles for Vibe Coding Accelerator Frontend */

/* Import Angular Material core styles - using the built-in theme */
@import '~@angular/material/prebuilt-themes/indigo-pink.css';

/* Alternative themes available:
   @import '~@angular/material/prebuilt-themes/deeppurple-amber.css';
   @import '~@angular/material/prebuilt-themes/pink-bluegrey.css';
   @import '~@angular/material/prebuilt-themes/purple-green.css';
*/

